package server;import java.sql.*;public class TestBase2 {    private static Connection connection;    private static Statement stmt;    public static void main(String[] args) {        try {            connect();            System.out.println("OK");            System.out.println(getNicknameByLoginAndPassword("vvv", "vvv"));        } catch (Exception ex) {            ex.printStackTrace();        } finally {            disconnect();        }    }    public static void connect() throws Exception {        Class.forName("org.sqlite.JDBC");        connection = DriverManager.getConnection("jdbc:sqlite:login3.db");        stmt = connection.createStatement();    }    public static void disconnect() {        try {            stmt.close();            connection.close();        } catch (SQLException e) {            e.printStackTrace();        }    }    public static String getNicknameByLoginAndPassword(String login, String password) throws SQLException {        ResultSet rs = stmt.executeQuery("SELECT * FROM auth");        while (rs.next()) {            if (rs.getString("login").equals(login) && rs.getString("password").equals(password))                return rs.getString("nickname");        }        return null;    }}